/* eslint-disable no-console */

'use strict'

const Logger = {
  log(...args) {
    console.log(...args)
  },

  info(...args) {
    console.log('INFO', ...args)
  },

  warn(...args) {
    console.warn('WARN', ...args)
  },

  debug(...args) {
    console.debug('DEBUG', ...args)
  },

  error(...args) {
    console.error('ERROR', ...args)
  }
}

module.exports = Logger